package gcfv2.datastore_api;import com.google.cloud.firestore.DocumentReference;import com.google.cloud.firestore.Firestore;import com.google.cloud.firestore.FirestoreOptions;public class Initializer {    public static Firestore getDataBase (String projectId , String databaseId) {        try {            FirestoreOptions firestoreOptions = FirestoreOptions.newBuilder()                    .setProjectId(projectId)                    .setDatabaseId(databaseId)                    .build();            Firestore db = firestoreOptions.getService();            DocumentReference docRef = db.collection("Ticket").document("Ticket1");            return db;        } catch (Exception e) {            e.printStackTrace();            return null;        }    }}