package org.planning.rest_api;import com.google.cloud.functions.HttpFunction;import com.google.cloud.functions.HttpRequest;import com.google.cloud.functions.HttpResponse;import java.io.IOException;import java.util.logging.Logger;public class MyHttpFunction implements HttpFunction {    private static final Logger logger = Logger.getLogger(MyHttpFunction.class.getName());    private final TicketService ticketService = new TicketService();    @Override    public void service(HttpRequest request, HttpResponse response) throws IOException {        try {            ticketService.service(request, response);        } catch (Exception e) {            logger.severe("Error handling HTTP request: " + e.getMessage());            response.setStatusCode(500);            response.getWriter().write("Internal Server Error");        }    }}